import React, { useState } from "react";
import Cart from "./components/Cart/Cart";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import CartProvider from "./store/CartProvider";

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };
  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  async function addMealHandler(movie) {
    // api generated by firebase. movies.json is required for name of database it autoamtically created
    const response = await fetch(
      "https://react-http-9a2e3-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json",
      {
        method: "POST",
        body: JSON.stringify(movie), // transform object to json
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    const data = await response.json();
    console.log(data)
  }





  return (
    <CartProvider>
      <button>Add Meal </button>
      {cartIsShown && <Cart onHideCart={hideCartHandler} />}

      <Header onShowCart={showCartHandler} />
      <main>
        <Meals />
      </main>
    </CartProvider>
  );
}

export default App;
