import React, { useState, useEffect, useCallback } from "react";
import Cart from "./components/Cart/Cart";
import Header from "./components/Layout/Header";
import Meals from "./components/Meals/Meals";
import CartProvider from "./store/CartProvider";

function App() {
  const [cartIsShown, setCartIsShown] = useState(false);
  const [meals, setMeals] = useState([])

  const showCartHandler = () => {
    setCartIsShown(true);
  };
  const hideCartHandler = () => {
    setCartIsShown(false);
  };

   const getMeals = useCallback(async () => {

    // api generated by firebase. movies.json is required for name of database it autoamtically created
    const response = await fetch(
      "https://react-http-9a2e3-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json"      
    );
    const data = await response.json();
    console.log("data",data)
    const availableMeals = [];
    for(const i in data){
      availableMeals.push({
        id:i,
        name:data[i].name,
        description:data[i].description,
        price:data[i].price
      })
    }

    setMeals(availableMeals);
  },[]);

  useEffect(() => {
    getMeals()
  },[getMeals])





  return (

    <CartProvider>
      {cartIsShown && <Cart onHideCart={hideCartHandler} />}

      <Header onShowCart={showCartHandler} />

      <main>
        <Meals meals={meals}/>
      </main>
    </CartProvider>
  );
}

export default App;
