import React,{useState, useEffect, useCallback} from "react";
import styles from "./AvailableMeals.module.css";
import { DUMMY_MEALS } from "../../assets/dummy-meals";
import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";

const AvailableMeals = (props) => {

  const[meals, setMeals] = useState([]);

  const getMeals = useCallback(async () => {

    // api generated by firebase. movies.json is required for name of database it autoamtically created
    const response = await fetch(
      "https://react-http-9a2e3-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json"      
    );
    const data = await response.json();
    console.log("data",data)
    const availableMeals = [];
    for(const i in data){
      availableMeals.push({
        id:i,
        name:data[i].name,
        description:data[i].description,
        price:data[i].price
      })
    }
    
    setMeals(availableMeals);
  },[]);

  useEffect(() => {
    getMeals()
  },[getMeals])



  const mealsList = meals.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      price={meal.price}
      description={meal.description}
    />
  ));
  return (
    <section className={styles.meals}>
      <Card>
        {mealsList.length > 0 && <ul>{mealsList}</ul>}
        {mealsList.length === 0 && <p>loading...</p>}
      </Card>
    </section>
  );
};

export default AvailableMeals;
