import { useReducer } from "react";
import CartContext from "./cart-context";

const defaultCartState = {
  items: [],
  totalAmount: 0,
};
// state is the latest state snapshot of the state managed by the reducer
// action is the saction dispatched by you later in yur code
const cartReducer = (state, action) => {
  if (action.type === "ADD") {
    //findIndex to find the index of item in array
    // this will return the index of the item if it exists
    // check if item added alraedy exists in state.items and return the index of it
    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.item.id
    );
    //get existing carte item by the index returned above
    const existingCartItem = state.items[existingCartItemIndex];
    let updatedItems;
    // if existingCartItem then updatedItem id=s equal to existingCartItem but updted amount
    if (existingCartItem) {
      const updatedItem = {
        ...existingCartItem,
        amount: existingCartItem.amount + action.item.amount,
      };
      console.log("state.items",state.items)
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;
    return {
      items: updatedItems,
      totalAmount: updatedTotalAmount,
    };
  }

  return defaultCartState;
};

const CartProvider = (props) => {
  // the first element is always the state snapshot
  // the second element is the function that allows you to dispatch action to the reducer
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );
  const addItemToCartHandler = (item) => {
    dispatchCartAction({ type: "ADD", item: item });
    console.log("Add");
  };
  const removeItemFromCartHandler = (id) => {
    dispatchCartAction({ type: "ADD", id: id });
    console.log("Remove");
  };

  const cartContext = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemToCartHandler,
    removeItem: removeItemFromCartHandler,
  };

  return (
    <CartContext.Provider value={cartContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
